import React, { useState, useContext, useEffect } from 'react';
import styles from './styles.module.scss';
import { getDataForMap } from '../../api';
import Loader from '../../components/Loader';
import { IVirtualizationResponse, IError } from '../../models';
import Globe from 'react-globe.gl';

const WorldMap: React.FC = () => {
  const [isLoaded, handleLoaded] = useState<boolean>(false);
  const [data, handleData] = useState<IVirtualizationResponse>();
  const [error, handleError] = useState<IError | undefined>(undefined);

  const fetchData = async () => {
    try {
      const data = await getDataForMap();
      handleData(data);
    } catch (e) {
      handleError(e);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    if (data) {
      handleLoaded(true);
    }
  }, [data]);

  if (error) {
    return <div>Something went wrong </div>;
  }
  if (!isLoaded) {
    return <Loader />;
  }

  return (
    <div className={styles.worldMapContainer}>
      <Globe globeImageUrl="//cdn.jsdelivr.net/npm/three-globe/example/img/earth-dark.jpg" />
    </div>
  );
};

export default WorldMap;
