import React, { useState, useContext, useEffect, useMemo } from 'react';
import styles from './styles.module.scss';
import { getDataForMap, getD3Data } from '../../api';
import Loader from '../../components/Loader';
import { IVirtualizationResponse, IError, IFeature } from '../../models';
import Globe from 'react-globe.gl';
import d3 from 'd3';

const WorldMap: React.FC = () => {
  const [isLoaded, handleLoaded] = useState<boolean>(false);
  const [data, handleData] = useState<IVirtualizationResponse>();
  const [error, handleError] = useState<IError | undefined>(undefined);
  const [countries, setCountries] = useState<IFeature[]>();
  const [hoverD, setHoverD] = useState();

  const fetchData = async () => {
    try {
      const data = await getDataForMap();
      const featureData = await getD3Data();
      handleData(data);
      setCountries(featureData);
    } catch (e) {
      handleError(e);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);
  //   const colorScale = d3.scaleSequentialSqrt(d3.interpolateYlOrRd);

  useEffect(() => {
    if (data && countries) {
      countries.forEach((country: IFeature) => {
        const dataCountriesValues = Object.values(data.countries);
        for (let i = 0; i < dataCountriesValues.length; i++) {
          if (dataCountriesValues[i].name === country.properties.SOVEREIGNT) {
            return (country.statistic = dataCountriesValues[i]);
          }
        }
      });
      handleLoaded(true);
    }
  }, [data, countries]);
  // const maxVal = useMemo(() => Math.max(...countries.features.map(getVal)), [countries]);
  //   const getVal = (feat) => feat.properties.GDP_MD_EST / Math.max(1e5, feat.properties.POP_EST);
  //   colorScale.domain([0, maxVal]);

  const getColor = (feature: IFeature): string => {
    if (feature.statistic) {
      const maxCases = 200000;
      const multipier = feature.statistic.cases / maxCases;
      return `rgba(200, 0, 0, ${multipier})`;
    } else {
      return `rgba(200, 0, 0, 0)`;
    }
  };
  const getLabelString = (properties: IFeature): string => {
    if (properties.statistic)
      return `<div> ${properties.statistic.name}</div>
    `;
    else
      return `
      <div>${properties.properties.NAME_SORT}</div>
    `;
  };

  if (error) {
    return <div>Something went wrong </div>;
  }
  if (!isLoaded) {
    return <Loader />;
  }
  return (
    <div className={styles.worldMapContainer}>
      <Globe
        globeImageUrl="//unpkg.com/three-globe/example/img/earth-dark.jpg"
        backgroundImageUrl="//unpkg.com/three-globe/example/img/night-sky.png"
        polygonsData={countries}
        polygonAltitude={(d) => (d === hoverD ? 0.12 : 0.06)}
        polygonCapColor={(d) => getColor(d)}
        polygonSideColor={(d: IFeature) => 'rgba(0, 100, 0, 0.15)'}
        polygonStrokeColor={() => 'red'}
        polygonLabel={(item) => getLabelString(item)}
        onPolygonHover={setHoverD}
        onPolygonClick={(r) => console.log(r)}
        polygonsTransitionDuration={300}
      />
    </div>
  );
};

export default WorldMap;
