import React, { useState, useContext, useEffect } from 'react';
import { ITimeLineResponse, INews } from '../../models';
// import styles from './styles.module.scss';
// import { getTimeLineNews } from '../../api';
// import { ITimeLineResponse } from '../../models';

export interface INewsBlockItemProps {
  data: ITimeLineResponse;
}

const NewsBlock: React.FC<INewsBlockItemProps> = (props) => {
  const { time, date } = props.data;
  const renderNewsItems = () => {
    if (time.length) {
      return time.map((newsItem: INews, counter: number) => (
        <div key={counter}>
          <h5>
            <a href={newsItem.source}>{newsItem.description}</a>
          </h5>
        </div>
      ));
    }
  };
  return (
    <div>
      <h3>{date}</h3>
      {renderNewsItems()}
    </div>
  );
};

export default NewsBlock;
