import React, { useState, useContext, useEffect } from 'react';
import styles from './styles.module.scss';
import { getTimeLineNews } from '../../api';
import { ITimeLineResponse } from '../../models';

const HomePage: React.FC = () => {
  const [news, setNewsList] = useState<ITimeLineResponse[]>();
  const [isLoaded, handleLoaded] = useState<boolean>(false);

  const fetchNews = async () => {
    const newsResponse = await getTimeLineNews();
    setNewsList(newsResponse);
  };

  useEffect(() => {
    fetchNews();
  }, []);

  useEffect(() => {
    if (news) {
      handleLoaded(true);
    }
  }, [news]);

  const renderNews = () => {
    if (news && news.length) {
      return news.map((newsItem: ITimeLineResponse) => <div key={newsItem.date}>{newsItem.date}</div>);
    } else <div>No news yet</div>;
  };

  if (!isLoaded) {
    return <div></div>;
  }

  return <>renderNews()</>;
};

export default HomePage;
