import React, { useState, useContext, useEffect, useMemo } from 'react';
import styles from './styles.module.scss';
import { getDataForMap } from '../../api';
import Loader from '../../components/Loader';
import { IVirtualizationResponse, IError } from '../../models';
import Globe from 'react-globe.gl';
import d3 from 'd3';

const WorldMap: React.FC = () => {
  const [isLoaded, handleLoaded] = useState<boolean>(false);
  const [data, handleData] = useState<IVirtualizationResponse>();
  const [error, handleError] = useState<IError | undefined>(undefined);

  const fetchData = async () => {
    try {
      const data = await getDataForMap();
      handleData(data);
    } catch (e) {
      handleError(e);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);
  const colorScale = d3.scaleSequentialSqrt(d3.interpolateYlOrRd);

  useEffect(() => {
    if (data) {
      handleLoaded(true);
    }
  }, [data]);
  const maxVal = useMemo(() => Math.max(...countries.features.map(getVal)), [countries]);
  colorScale.domain([0, maxVal]);

  if (error) {
    return <div>Something went wrong </div>;
  }
  if (!isLoaded) {
    return <Loader />;
  }

  return (
    <div className={styles.worldMapContainer}>
      <Globe
        globeImageUrl="//unpkg.com/three-globe/example/img/earth-night.jpg"
        backgroundImageUrl="//unpkg.com/three-globe/example/img/night-sky.png"
        // polygonsData={countries.features}
        // polygonAltitude={(d) => (d === hoverD ? 0.12 : 0.06)}
        // polygonCapColor={(d) => (d === hoverD ? 'steelblue' : colorScale(getVal(d)))}
        polygonSideColor={() => 'rgba(0, 100, 0, 0.15)'}
        polygonStrokeColor={() => '#111'}
        polygonLabel={({ properties: d }) => `
        <b>${d.ADMIN} (${d.ISO_A2}):</b> <br />
        GDP: <i>${d.GDP_MD_EST}</i> M$<br/>
        Population: <i>${d.POP_EST}</i>
      `}
        onPolygonHover={setHoverD}
        polygonsTransitionDuration={300}
      />
    </div>
  );
};

export default WorldMap;
